DROP TABLE IF EXISTS `app_article`;
CREATE TABLE `app_article`  (
  `id` int(0) NOT NULL AUTO_INCREMENT,
  `category_id` int(0) NULL DEFAULT NULL COMMENT '所属分类',
  `user_id` int(0) NOT NULL COMMENT '文章发布者id，系统管理员',
  `author` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '作者名字',
  `title` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文章标题',
  `desc` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '文章简介',
  `type` tinyint(0) NOT NULL DEFAULT 1 COMMENT '文章类型，1表示原创，2表示转载，3表示翻译',
  `url` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '文章链接',
  `allow_comment` tinyint(1) NULL DEFAULT 0 COMMENT '是否允许评论,默认否0，是1',
  `copyfrom` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '转载外链',
  `sort` tinyint(0) NOT NULL DEFAULT 50 COMMENT '排序',
  `top` tinyint(1) NOT NULL DEFAULT 0 COMMENT '是否置顶',
  `read` int(0) NOT NULL DEFAULT 0 COMMENT '阅读数',
  `praise` int(0) NOT NULL DEFAULT 0 COMMENT '点赞数',
  `create_time` datetime(0) NULL DEFAULT NULL COMMENT '发布时间',
  `delete_time` datetime(0) NULL DEFAULT NULL COMMENT '删除时间',
  `content` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '文章内容',
  `cover_img` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '文章封面',
  `update_time` datetime(0) NULL DEFAULT NULL COMMENT '更新时间',
  `status` tinyint(0) NOT NULL DEFAULT 1 COMMENT '0表示停用1启用',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 30 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

